import React, { useEffect, useState } from 'react';

const AllEvent = ({ event }) => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:8080/tasks');
        if (!response.ok) {
          throw new Error('Failed to fetch tasks');
        }
        const data = await response.json();
        // Filter tasks for the current event
        const eventTasks = data.filter(task => task.event.id === event.id);
        setTasks(eventTasks);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (event?.id) {
      fetchTasks();
    }
  }, [event]);

  // Calculate progress
  const calculateProgress = () => {
    if (tasks.length === 0) return 0;
    const completedTasks = tasks.filter(task => task.status === 'Completed').length;
    return Math.round((completedTasks / tasks.length) * 100);
  };

  if (loading) return <div className="text-white">Loading...</div>;
  if (error) return <div className="text-red-500">Error: {error}</div>;

  // Get unique attendees from tasks
  const getUniqueAttendees = () => {
    const attendees = tasks
      .map(task => task.attendee)
      .filter(attendee => attendee !== null);
    return [...new Map(attendees.map(item => [item.id, item])).values()];
  };

  return (
    <div className="card bg-gray-800 text-white mb-4 p-4 rounded-lg shadow">
      <div className="flex justify-between items-start mb-3">
        <div>
          <h3 className="text-xl font-bold mb-2">{event.name}</h3>
          <p className="text-gray-400 mb-3">{event.description}</p>
          <div className="space-y-2">
            <div className="text-sm text-gray-400">Location: {event.location}</div>
            <div className="text-sm text-gray-400">
              Date: {new Date(event.date).toLocaleDateString()}
            </div>
          </div>
        </div>
      </div>

      {/* Progress Section */}
      <div className="mt-4">
        <div className="flex justify-between items-center mb-2">
          <span className="text-sm text-gray-400">Tasks Progress</span>
          <span className="text-sm text-gray-400">{calculateProgress()}%</span>
        </div>
        <div className="w-full bg-gray-700 rounded-full h-2">
          <div
            className="bg-purple-600 h-2 rounded-full"
            style={{ width: `${calculateProgress()}%` }}
          ></div>
        </div>
      </div>

      {/* Attendees Section */}
      <div className="mt-4">
        <h4 className="text-sm font-semibold mb-2">Attendees</h4>
        <div className="flex flex-wrap gap-2">
          {getUniqueAttendees().map((attendee) => (
            <div
              key={attendee.id}
              className="flex items-center bg-gray-700 rounded-full px-3 py-1"
            >
              <span className="text-sm">{attendee.name}</span>
              <span className="ml-2 w-2 h-2 bg-green-500 rounded-full"></span>
            </div>
          ))}
        </div>
      </div>

      {/* Tasks Section */}
      <div className="mt-4">
        <h4 className="text-sm font-semibold mb-2">Tasks</h4>
        <div className="space-y-2">
          {tasks.map((task) => (
            <div
              key={task.id}
              className="bg-gray-700 p-4 rounded-lg mb-3 flex justify-between items-center"
            >
              <div className="flex-1">
                <h5 className="text-lg font-bold text-white">{task.name}</h5>
                <p className="text-gray-400 text-sm">
                  Deadline: {new Date(task.deadline).toLocaleDateString()}
                </p>
                <p className="text-sm text-gray-400">Status: {task.status}</p>
              </div>

              {/* Attendee Info */}
              <div className="flex items-center">
                <span className="text-sm text-gray-400 mr-2">Assigned to:</span>
                <span className="text-sm text-white">
                  {task.attendee ? task.attendee.name : 'Unassigned'}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AllEvent;
