import React from "react";

const CalendarView = ({ events }) => {
  const today = new Date();
  const month = today.getMonth();
  const year = today.getFullYear();

  const daysInMonth = new Date(year, month + 1, 0).getDate();

  return (
    <div className="border p-4 rounded-lg bg-gray-800">
      <h3 className="text-xl font-semibold text-white mb-4">Calendar</h3>
      <div className="grid grid-cols-7 gap-2">
        {Array.from({ length: daysInMonth }).map((_, day) => {
          const currentDate = new Date(year, month, day + 1);
          const eventsOnThisDay = events.filter((event) => {
            const eventDate = new Date(event.date);
            return eventDate.getDate() === currentDate.getDate();
          });

          return (
            <div
              key={day}
              className={`p-2 rounded-lg ${eventsOnThisDay.length > 0 ? "bg-blue-500" : "bg-gray-600"}`}
            >
              {day + 1}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default CalendarView;
