import React, { useState, useEffect } from "react";
import { getAttendees, removeAttendee } from "../"; // Import both functions
import AttendeeCard from "../components/AttendeeCard";

const Attendees = () => {
  const [attendees, setAttendees] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch attendees from the server
  const fetchAttendees = async () => {
    try {
      const response = await getAttendees();
      setAttendees(response.data); // Assuming `response.data` contains the attendee list
      setLoading(false);
    } catch (error) {
      console.error("Error fetching attendees:", error);
      setLoading(false);
    }
  };

  // Fetch attendees on component mount
  useEffect(() => {
    fetchAttendees();
  }, []);

  // Delete attendee and refetch the updated list
  const handleDelete = async (attendeeId) => {
    try {
      await removeAttendee(attendeeId); // Delete attendee by ID
      await fetchAttendees(); // Fetch the updated attendee list
    } catch (error) {
      console.error("Error deleting attendee:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Attendees</h1>
      {loading ? (
        <p>Loading attendees...</p>
      ) : attendees.length > 0 ? (
        <div className="space-y-4">
          {attendees.map((attendee) => (
            <AttendeeCard
              key={attendee.id}
              attendee={attendee}
              onDelete={handleDelete} // Pass the delete handler to AttendeeCard
            />
          ))}
        </div>
      ) : (
        <p className="text-gray-500">No attendees found.</p>
      )}
    </div>
  );
};

export default Attendees;
