import React, { useState, useEffect } from "react";
import axios from "axios";

const AttendeeCard = () => {
  const [attendees, setAttendees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch attendees data
  const fetchAttendees = async () => {
    try {
      const response = await axios.get("http://localhost:8080/attendees");
      setAttendees(response.data);
      setLoading(false);
    } catch (err) {
      setError("Failed to load attendees.");
      console.error("Error fetching attendees:", err);
      setLoading(false);
    }
  };

  // Handle component mount
  useEffect(() => {
    fetchAttendees();
  }, []);

  // Handle delete operation
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/attendees/${id}`);
      setAttendees(attendees.filter((attendee) => attendee.id !== id));
    } catch (error) {
      console.error("Error deleting attendee:", error);
    }
  };

  if (loading) {
    return <p className="text-gray-400 text-center">Loading attendees...</p>;
  }

  if (error) {
    return <p className="text-red-500 text-center">{error}</p>;
  }

  if (attendees.length === 0) {
    return (
      <p className="text-gray-400 text-center">No attendees found.</p>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {attendees.map((attendee) => (
        <div key={attendee.id} className="bg-gray-700 text-white p-6 rounded-lg shadow-lg">
          <div className="flex justify-between items-center">
            <h3 className="text-2xl font-semibold">{attendee.name}</h3>
            <button
              className="bg-red-500 hover:bg-red-700 px-4 py-2 rounded"
              onClick={() => handleDelete(attendee.id)}
            >
              Delete
            </button>
          </div>
          <p className="mt-4">{attendee.email}</p>
          <p className="text-sm mt-2">
            Event: {attendee.event.name} ({attendee.event.location})
          </p>
        </div>
      ))}
    </div>
  );
};

export default AttendeeCard;
