import React, { useEffect, useState } from 'react';
import { getTasks, getAttendees } from '..';

const EventCard = ({ event }) => {
  const [tasks, setTasks] = useState([]);
  const [attendees, setAttendees] = useState([]);

  // Fetch tasks and attendees on component mount
  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const taskResponse = await getTasks(event.id);
        setTasks(taskResponse.data);

        const attendeeResponse = await getAttendees(event.id);
        setAttendees(attendeeResponse.data);
      } catch (error) {
        console.error('Error fetching event details:', error);
      }
    };
    fetchDetails();
  }, [event.id]);

  return (
    <div className="card bg-gray-800 text-white mb-4 p-4 rounded-lg shadow">
      <div className="flex justify-between items-start mb-3">
        <div>
          <h3 className="text-xl font-bold mb-2">{event.name}</h3>
          <p className="text-gray-400 mb-3">{event.description}</p>
          <div className="space-y-2">
            <div className="flex items-center text-sm text-gray-400">
              <span className="mr-2">Location: {event.location}</span>
            </div>
            <div className="flex items-center text-sm text-gray-400">
              <span className="mr-2">Date: {new Date(event.date).toLocaleDateString()}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Tasks Section */}
      <div className="mt-4">
        <div className="flex justify-between items-center mb-2">
          <span className="text-sm text-gray-400">Tasks</span>
          <span className="text-sm text-gray-400">
            {tasks.filter((task) => task.completed).length}/{tasks.length} Completed
          </span>
        </div>
        <div className="space-y-2">
          {tasks.map((task, index) => (
            <div key={index} className="flex items-center justify-between">
              <span className="text-sm">{task.name}</span>
              <div className="w-1/2 bg-gray-700 rounded-full h-1.5">
                <div
                  className="bg-purple-600 h-1.5 rounded-full"
                  style={{ width: `${task.progress}%` }}
                ></div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Attendees Section */}
      <div className="mt-4">
        <h4 className="text-sm font-semibold mb-2">Attendees</h4>
        <div className="flex flex-wrap gap-2">
          {attendees.map((attendee, index) => (
            <div key={index} className="flex items-center bg-gray-700 rounded-full px-3 py-1">
              <span className="text-sm">{attendee.name}</span>
              {attendee.isAssigned && (
                <span className="ml-2 w-2 h-2 bg-green-500 rounded-full"></span>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventCard;
