import React, { useState, useEffect } from "react";
import { getAttendees } from "../utilities/aip";
import AttendeeCard from "../component/AttendeeCard";
import AttendeeForm from "../components/AttendeeForm";

const Attendees = () => {
  const [attendees, setAttendees] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    getAttendees().then((response) => setAttendees(response.data));
  }, []);

  const handleAddAttendee = (newAttendee) => {
    setAttendees([...attendees, newAttendee]);
    setShowForm(false);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Attendees</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-green-500 hover:bg-green-700 text-white px-4 py-2 rounded"
        >
          Add Attendee
        </button>
      </div>
      {showForm && <AttendeeForm onSubmit={handleAddAttendee} />}
      <div className="space-y-6 mt-6">
        {attendees.map((attendee) => (
          <AttendeeCard key={attendee.id} attendee={attendee} />
        ))}
      </div>
    </div>
  );
};

export default Attendees;
