import React from 'react';

const AllEvent = ({ event }) => {
  return (
    <div className="card bg-gray-800 text-white mb-4 p-4 rounded-lg shadow">
      <div className="flex justify-between items-start mb-3">
        <div>
          <h3 className="text-xl font-bold mb-2">{event.name}</h3>
          <p className="text-gray-400 mb-3">{event.description}</p>
          <div className="space-y-2">
            <div className="text-sm text-gray-400">Location: {event.location}</div>
            <div className="text-sm text-gray-400">
              Date: {new Date(event.date).toLocaleDateString()}
            </div>
          </div>
        </div>
      </div>

      {/* Progress Section */}
      <div className="mt-4">
        <div className="flex justify-between items-center mb-2">
          <span className="text-sm text-gray-400">Tasks Progress</span>
          <span className="text-sm text-gray-400">{event.progress || 0}%</span>
        </div>
        <div className="w-full bg-gray-700 rounded-full h-2">
          <div
            className="bg-purple-600 h-2 rounded-full"
            style={{ width: `${event.progress || 0}%` }}
          ></div>
        </div>
      </div>

      {/* Attendees Section */}
      <div className="mt-4">
        <h4 className="text-sm font-semibold mb-2">Attendees</h4>
        <div className="flex flex-wrap gap-2">
          {event.attendees?.map((attendee, index) => (
            <div
              key={index}
              className="flex items-center bg-gray-700 rounded-full px-3 py-1"
            >
              <span className="text-sm">{attendee.name}</span>
              {attendee.isAssigned && (
                <span className="ml-2 w-2 h-2 bg-green-500 rounded-full"></span>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AllEvent;
