import React, { useState, useEffect } from "react";
import Calendar from "react-calendar"; // Import calendar library
import "react-calendar/dist/Calendar.css"; // Default styles for the calendar

const EventCalendar = () => {
  const [events, setEvents] = useState([]);
  const [markedDates, setMarkedDates] = useState([]);

  useEffect(() => {
    // Fetch events from your database
    const fetchEvents = async () => {
      // Simulated API call; replace with your actual API logic
      const eventData = await fetch("/api/events").then((res) => res.json());
      setEvents(eventData);

      // Extract dates for marking on the calendar
      const dates = eventData.map((event) => new Date(event.date));
      setMarkedDates(dates);
    };

    fetchEvents();
  }, []);

  const tileContent = ({ date }) => {
    // Check if the date is marked as an event
    if (markedDates.some((markedDate) => markedDate.toDateString() === date.toDateString())) {
      return <div className="bg-red-500 w-2 h-2 rounded-full mx-auto mt-1"></div>;
    }
    return null;
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4 text-white">Event Calendar</h2>
      <Calendar
        tileContent={tileContent}
        className="react-calendar bg-black text-white rounded-lg shadow-lg"
        // Additional styles for calendar
        tileClassName={({ date }) =>
          markedDates.some((markedDate) => markedDate.toDateString() === date.toDateString())
            ? "bg-red-500 text-white"  // Mark event dates in red
            : ""
        }
      />
    </div>
  );
};

export default EventCalendar;
