import React from "react";
import { updateTaskStatus } from "../utilities/aip"; // Make sure this function is implemented

const TaskCard = ({ task }) => {
  const handleStatusChange = (status) => {
    updateTaskStatus(task.id, status).then(() => {
      // Optionally update task status in the state or notify parent component
    });
  };



  return (
    <div className="bg-gray-700 text-white p-6 rounded-lg shadow-lg">
      <div className="flex justify-between items-center">
        <h3 className="text-2xl font-semibold">{task.name}</h3>
        <div className="flex space-x-4">
          <button
            className="bg-blue-500 hover:bg-blue-700 px-4 py-2 rounded"
            onClick={() => handleStatusChange("In Progress")}
          >
            In Progress
          </button>
          <button
            className="bg-green-500 hover:bg-green-700 px-4 py-2 rounded"
            onClick={() => handleStatusChange("Completed")}
          >
            Completed
          </button>
        </div>
      </div>
      <p className="mt-4">{task.description}</p>
      <div className="mt-4">
        {/* Add your task progress bar here */}
        <div className="w-full bg-gray-700 rounded-full h-2">
          <div
            className="bg-purple-600 h-2 rounded-full transition-all duration-300"
            style={{ width: `${task.progress}%` }}
          />
        </div>
      </div>
    </div>
  );
};

export default TaskCard;
