import React, { useState } from "react";
import { createEvent } from "../utilities/aip";

const EventForm = ({ onSubmit }) => {
  const [eventName, setEventName] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const eventData = {
      name: eventName,
      description,
      date,
    };
    createEvent(eventData).then((response) => onSubmit(response.data));
  };

  return (
    <form onSubmit={handleSubmit} className="bg-gray-800 p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-semibold text-white mb-6">Add Event</h2>
      <div className="space-y-4">
        <input
          type="text"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          className="w-full p-2 rounded-lg"
          placeholder="Event Name"
        />
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="w-full p-2 rounded-lg"
          placeholder="Event Description"
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="w-full p-2 rounded-lg"
          placeholder="Event Date"
        />
        <button type="submit" className="bg-green-500 hover:bg-green-700 px-6 py-2 rounded">
          Submit
        </button>
      </div>
    </form>
  );
};

export default EventForm;
