import React from "react";
import { getAttendees,removeAttendee } from "../utilities/aip";

const AttendeeCard = ({ attendee, onDelete }) => {
  const handleDelete = async () => {
    try {
      await removeAttendee(attendee.id);
      onDelete(attendee.id);
    } catch (error) {
      console.error("Error deleting attendee:", error);
    }
  };

  return (
    <div className="bg-gray-800 text-white p-6 rounded-lg shadow-lg transition-all duration-300 hover:shadow-xl hover:bg-gray-700">
      <div className="flex justify-between items-start">
        <div>
          <h3 className="text-2xl font-semibold text-white mb-2">{attendee.name}</h3>
          <p className="text-gray-300">{attendee.email}</p>
        </div>
        <button
          onClick={handleDelete}
          className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg transition-colors duration-300 flex items-center gap-2"
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
            />
          </svg>
          Delete
        </button>
      </div>
    </div>
  );
};

export default AttendeeCard;
