import axios from 'axios';

const API_URL = 'http://localhost:8080/api/events'; // Your backend URL

export const eventAPIService = {
  getAllEvents: () => axios.get(API_URL),
  createEvent: (event) => axios.post(`${API_URL}/create`, event),
  updateEvent: (id, event) => axios.put(`${API_URL}/update/${id}`, event),
  deleteEvent: (id) => axios.delete(`${API_URL}/delete/${id}`),
};
