import React, { useState, useEffect } from 'react';
import { attendeeAPIService } from '../../services/';

const AttendeeList = () => {
  const [attendees, setAttendees] = useState([]);

  useEffect(() => {
    const fetchAttendees = async () => {
      const result = await attendeeAPIService.getAllAttendees();
      setAttendees(result.data);
    };

    fetchAttendees();
  }, []);

  const handleDelete = async (id) => {
    await attendeeAPIService.deleteAttendee(id);
    window.location.reload(); // Refresh the page after deletion
  };

  return (
    <div>
      <h2>Attendee List</h2>
      {attendees.map((attendee) => (
        <div key={attendee.id}>
          <h4>{attendee.name}</h4>
          <p>{attendee.email}</p>
          <button onClick={() => handleDelete(attendee.id)}>Remove</button>
        </div>
      ))}
    </div>
  );
};

export default AttendeeList;
