import React, { useState } from "react";
import { updateEvent, deleteEvent } from "../utilities/aip";

const EventCard = ({ event }) => {
  const [showEdit, setShowEdit] = useState(false);

  const handleDelete = () => {
    deleteEvent(event.id).then(() => {
      // Handle success
    });
  };

  return (
    <div className="bg-gray-700 text-white p-6 rounded-lg shadow-lg">
      <div className="flex justify-between items-center">
        <h3 className="text-2xl font-semibold">{event.name}</h3>
        <div className="flex space-x-4">
          <button
            className="bg-blue-500 hover:bg-blue-700 px-4 py-2 rounded"
            onClick={() => setShowEdit(!showEdit)}
          >
            Edit
          </button>
          <button
            className="bg-red-500 hover:bg-red-700 px-4 py-2 rounded"
            onClick={handleDelete}
          >
            Delete
          </button>
        </div>
      </div>
      <p className="mt-4">{event.description}</p>

      {/* Displaying event location and date directly */}
      <div className="mt-4">
        <p className="text-sm">Location: {event.location}</p>
        <p className="text-sm">Date: {new Date(event.date).toLocaleDateString()}</p>
      </div>
    </div>
  );
};

export default EventCard;
