import axios from 'axios';

const API_URL = 'http://192.168.1.34:8080/api/tasks'; // Your backend URL

export const taskAPIService = {
  getTasksForEvent: (eventId) => axios.get(`${API_URL}/event/${eventId}`),
  createTask: (task) => axios.post(API_URL, task),
  updateTaskStatus: (taskId, status) => axios.put(`${API_URL}/${taskId}/status`, { status }),
};
